class Run(QThread):
    def __init__(self, main_window):
        super().__init__()
        self.main_window = main_window

    def run(self):
        self.main_window.savepath = QFileDialog.getExistingDirectory(self.main_window,
                                                                     'Choose a folder to save the result')
        # command = 'python3 detect.py --source ' + self.main_window.path + ' --project ' + self.main_window.savepath + \
        #           ' --device ' + self.main_window.device + ' --weights ' + self.main_window.model
        # print(command)
        # os.system(command)
        self.main_window.detect.run(source=self.main_window.path, weights=self.main_window.model,
                                    device=self.main_window.device, project=self.main_window.savepath)
        # if (file_name[0] == ""):
        #     QMessageBox.information(self, "提示", self.tr("没有选择图片文件！"))
        p = Path(self.main_window.path)
        s = self.main_window.savepath + '/' + p.name
        if s.endswith(".jpg") or s.endswith(".png") or s.endswith(".jpeg"):
            img = cv2.imread(s)  # 读取图像
            img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)  # 转换图像通道
            img_x = img.shape[1]  # 获取图像大小
            img_y = img.shape[0]
            # x = 0
            # y = 0
            ratio = img_x / img_y
            window_x = self.main_window.graphicsView.width()
            window_y = self.main_window.graphicsView.height()
            if ratio * window_y <= window_x:
                x = int(ratio * (window_y - 20))
                y = window_y - 20
            else:
                x = window_x - 20
                y = int((window_x - 20) / ratio)
            print("resize")
            img = cv2.resize(img, (x, y))
            frame = QImage(img, x, y, QImage.Format_RGB888)
            pix = QPixmap.fromImage(frame)
            self.main_window.item = QGraphicsPixmapItem(pix)  # 创建像素图元
            # self.item.setScale(self.zoomscale)
            self.main_window.scene = QGraphicsScene()  # 创建场景
            self.main_window.scene.addItem(self.main_window.item)
            self.main_window.graphicsView.setScene(self.main_window.scene)  # 将场景添加至视图


class Open_img(QThread):
    def __init__(self, main_window):
        super().__init__()
        self.main_window = main_window

    def run(self):
        file_name = QFileDialog.getOpenFileName(self.main_window, 'Choose file', './', 'image(*.jpg , *.png)')
        image_path = file_name[0]
        # if (file_name[0] == ""):
        #     QMessageBox.information(self, "提示", self.tr("没有选择图片文件！"))
        print(image_path)
        self.main_window.path = image_path
        img = cv2.imread(image_path)  # 读取图像
        img = cv2.cvtColor(img, cv2.COLOR_BGR2RGB)  # 转换图像通道
        img_x = img.shape[1]  # 获取图像大小
        img_y = img.shape[0]
        ratio = img_x / img_y
        window_x = self.main_window.graphicsView.width()
        window_y = self.main_window.graphicsView.height()
        if ratio * window_y <= window_x:
            x = int(ratio * (window_y - 20))
            y = window_y - 20
        else:
            x = window_x - 20
            y = int((window_x - 20) / ratio)
        print("resize")
        img = cv2.resize(img, (x, y))
        frame = QImage(img, x, y, QImage.Format_RGB888)
        pix = QPixmap.fromImage(frame)
        self.main_window.item = QGraphicsPixmapItem(pix)  # 创建像素图元
        # self.item.setScale(self.zoomscale)
        self.main_window.scene = QGraphicsScene()  # 创建场景
        self.main_window.scene.addItem(self.main_window.item)
        self.main_window.graphicsView.setScene(self.main_window.scene)  # 将场景添加至视图


